pragma solidity ^0.4.21;

import "./SafeMath.sol";
import "./Token.sol";

contract CrowdFunding {
    using SafeMath for uint256;

    ERC223 public token;
    address public wallet = 0x9D9C605fF54425876Cda6AE3BD4c2c408A713bf1;
    uint256 public rate = 1000;
    uint256 public weiRaised;
    uint256 public openingTime;

    event TokenPurchase(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);
    event TokenAmount(uint256 indexed amount);

    function CrowdFunding() public {
        openingTime = 1525419946;
        token = new Token(address(this));
    }

    function getBalance() public view returns (uint) {
        return token.balanceOf(msg.sender);
    }

    modifier onlyAfterOpening {
        require(openingTime >= block.timestamp);
        _;
    }

    function () external payable {
        buyTokens(msg.sender);
    }

    function buyTokens(address _beneficiary) public payable {
        uint256 weiAmount = msg.value;
        _preValidatePurchase(_beneficiary, weiAmount);

        uint256 tokens = _getTokenAmount(weiAmount);

        weiRaised = weiRaised.add(weiAmount);

        _processPurchase(_beneficiary, tokens);
        emit TokenPurchase(
        msg.sender,
        _beneficiary,
        weiAmount,
        tokens
        );

        _forwardFunds();
    }

    function _preValidatePurchase(address _beneficiary, uint256 _weiAmount) internal {
        require(_beneficiary != address(0));
        require(_weiAmount != 0);
    }

    function _deliverTokens(address _beneficiary, uint256 _tokenAmount) internal {
        token.transfer(_beneficiary, _tokenAmount, new bytes(0));
    }

    function _processPurchase(address _beneficiary, uint256 _tokenAmount) internal {
        _deliverTokens(_beneficiary, _tokenAmount);
    }

    function _getTokenAmount(uint256 _weiAmount) internal view returns (uint256) {
        uint256 etherAmount = _weiAmount / 1000000000000000000;
        return etherAmount.mul(rate);
    }

    function _forwardFunds() internal {
        wallet.transfer(msg.value);
    }
}
